package week1.day1;

public class Datatypesandvariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String brandName= "Oneplus";
String model= "5T";
char color= 'B';
String price= "$450";
int rate= 40000;
byte memory= 64;
float screenSize= 10.3f;
boolean isTouchScreen= true;
long phNumb= 1234567890023L;

System.out.println("My Brand is " + brandName  +  " and model is " + model);
System.out.println("The color of my mobile is " + color);
System.out.println("The price is " + rate + " & in dollars it is " + price );
System.out.println("The memory & screensize are " + memory + " & " + screenSize + " respectively");
System.out.println("And also the mobile has " + isTouchScreen);
System.out.println("My phno is " + phNumb);


	}

}
