package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int factorial = 1;
		for (int i = 5; i >=1; i--) {
			
			
				System.out.println(" The Number is " + i );
			
				factorial= factorial*i;
				
			
		}
		
		System.out.println(" The factorial is " + factorial);
	}

}
